// import react, { Component } from 'react';
import '../../App.css';
// import axios from 'axios';
import React from 'react';
import { Link } from 'react-router-dom';
// import UserLoop from './userLoop';
// import React from 'react';
import { useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
// import { getUserList } from '../../redux/store/Actions/GetUserListAction';

function Users() {

    // const dispatch = useDispatch();

// const jwt_token = useSelector((state) => state.UserReducer.jwt_token);

  useEffect(() => {
    dispatch(getUserList());

  });

    const users = useSelector((state) => state.UserReducer.usersList);



    return (

                <div className="row">
                    <div className="col-md-10 col-sm-10">
                        <h2 className="display-4 text-center" >User List</h2>
                    </div>

                    <div className="col-md-2 col-sm-2">
                        <Link to="/user-sign-up" className="btn btn-outline-warning float-right">
                            + Add New User
                        </Link>
                    </div> 

                    <div className="col-md-12 table-responsive">
                         <table className="table table-striped table-bordered table-hover">
                            <thead>
                                <tr >
                                    <th>User Name</th>
                                    <th>Email Address</th>
                                    <th>User Type</th>
                                    <th>Role Id</th>
                                    <th>Created Date</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users && users.map((userData, index) => {
                                    // console.log(userData.firstName + ' ' + userData.lastName);
                                    return <tr key={index} >
                                        <td>{userData.role_name }</td>
                                        <td>{userData.user_name}</td>
                                        <td>{userData.user_type}</td>
                                        <td>{userData.roleId}</td>
                                        <td>{userData.created_date}</td>
                                        <td className="actionClass">
                                            {/* <Link to={`/edit/${userData._id}`}>Edit</Link> */}
                                            <Link to={`/user-record-edit/${userData.id}`}>Edit</Link>
                                            <Link to={`/user-record-delete/${userData.id}`}>Delete</Link>
                                        </td>
                                    </tr>
                                })}
                            </tbody>
                        </table>
                    </div>

                </div>



    );



}
export default Users;
