export const updateUser = (userId, updatedUserData) => {
  return async (dispatch) => {
    try {
      // Make API call to update user in the database
      await axios.put(`http://localhost:3002/api/users/${userId}`, updatedUserData); // Adjust the URL based on your API structure

      // Refetch the updated user list after the update is successful
      const response = await axios.get('http://localhost:3002/api/users'); // Replace with your actual API endpoint

      // Dispatch the action to update the Redux store with the new user list
      dispatch({
        type: LOAD_USERS,
        payload: response.data, // Updated list of users from the database
      });

      // Optionally, you can also dispatch an individual update if needed
      dispatch({
        type: UPDATE_USER,
        payload: { userId, updatedUserData },
      });

    } catch (error) {
      console.error('Error updating user: ', error);
      // Handle error (e.g., dispatch an error action)
    }
  };
};
// Action to delete a user
export const deleteUser = (id) => ({
  type: DELETE_USER,
  payload: { id }
});
