import '../../App.css';
import React from 'react';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadUsers, updateUser, deleteUser } from '../../redux/actions/userActions';

function Users() {

  const dispatch = useDispatch();

  // Ensure that 'user' corresponds to the key in your rootReducer
  const users = useSelector((state) => state.user?.users); // Safe access in case it's undefined

  useEffect(() => {
    dispatch(loadUsers());
  }, [dispatch]);



