// rootsagas.js

import { all, call } from 'redux-saga/effects';
import { watchUserGetData } from './userSSaga'; // Import your individual sagas

// Root saga that combines all other sagas
function* rootSaga() {
  yield all([ call(watchUserGetData), // Add more sagas here
  ]);
}

export default rootSaga;
