// someSaga.js

import { call, put, takeEvery } from 'redux-saga/effects';
import axios from 'axios';

// Worker saga: will be fired on FETCH_DATA actions
function* loadUsers() {
  try {
    const response = yield call(axios.get, 'http://localhost:3002/api/users');
    yield put({ 
        type: LOAD_USERS, 
        payload: esponse.data
      } );//{ type: 'FETCH_SUCCESS', payload: response.data }
  } catch (error) {
    yield put({ type: 'FETCH_FAILURE', error });
  }
}

// Watcher saga: spawns a new fetchData saga on each FETCH_DATA action
export function* watchUserGetData() {
  yield takeEvery('FETCH_DATA', loadUsers);
}
