// src/redux/actions/userActions.js
export const LOAD_USERS = 'LOAD_USERS';
export const UPDATE_USER = 'UPDATE_USER';
export const DELETE_USER = 'DELETE_USER';

// Action to load users
// export const loadUsers = (users) => ({
//   type: LOAD_USERS,
//   payload: users
// });

export const loadUsers = () => {

  return (dispatch) => {

    const token = 'emjjjdsjfj';//localStorage.getItem("jwtToken");
    // console.log("token===>", token);
    //   fetch placeholder data from jsonplaceholder
    fetch(`http://localhost:3002/api/users`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then((response) => response.json())
      .then((result) =>{
        //dispatch response to the store
        console.log("result===>", result);
        dispatch({ 
          type: LOAD_USERS, 
          payload: result
        })
      }

      );
  };


};

// Action to update a user
// export const updateUserOld = (updatedUser) => ({
//   type: UPDATE_USER,
//   payload: updatedUser
// });

export const updateUser = (userId, updatedUserData) => {

  return (dispatch, getState) => {

    const users = getState().user?.users;
    console.log("users===>", users);

    const updatedUsers = users.map((user) =>
      user.id === userId ? { ...user, ...updatedUserData } : user
    );

    console.log("users===>", updatedUsers);

    dispatch({
      type: UPDATE_USER,
      payload: updatedUsers,
    });
  };

};

// Action to delete a user
export const deleteUser = (id) => ({
  type: DELETE_USER,
  payload: { id }
});
