// rootsagas.js

import { all, call } from 'redux-saga/effects';
import { watchFetchData } from './someSaga'; // Import your individual sagas

// Root saga that combines all other sagas
function* rootSaga() {
  yield all([ call(watchFetchData), // Add more sagas here
  ]);
}

export default rootSaga;
