import '../../App.css';
import React from 'react';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadUsers, updateUser, deleteUser } from '../../redux/actions/userActions';

function Users() {

  const dispatch = useDispatch();

  // Ensure that 'user' corresponds to the key in your rootReducer
  const users = useSelector((state) => state.user?.users); // Safe access in case it's undefined

  useEffect(() => {
    const initialUsers = [
      {
        id: 1,
        role_name: 'Admin',
        user_name: 'Ankit Jain' ,
        user_type: 'Developer',
        roleId: 1,
        created_date: '2021-09-01'
      },
      {
        id: 2,
        role_name: 'User',
        user_name: 'Rohit Mehta',
        user_type: 'Manager',
        roleId: 2,
        created_date: '2021-09-02'
      },
      {
        id: 3,
        role_name: 'Admin',
        user_name: 'Samiran Das',
        user_type: 'Manager',
        roleId: 2,
        created_date: '2021-09-03'
      }
    ];
    dispatch(loadUsers(initialUsers));
  }, [dispatch]);



  const updateUserRecord = (userId) => {

    const updatedUser = {
      id: userId,
      role_name: 'Vice President',
      user_name: 'Anurag Sahni',
      user_type: 'Devops',
      roleId: 3,
      created_date: '2023-09-05'
    };

    dispatch(updateUser(updatedUser));
  };

  const deleteUserRecord = (userId) => {
    console.log('Delete User Record: ', userId);
    dispatch(deleteUser(userId));
  }


    return (

                <div className="row">
                    <div className="col-md-10 col-sm-10">
                        <h2 className="display-4 text-center" >User List</h2>
                    </div>

                    {/* <div className="col-md-2 col-sm-2">
                        <Link to="/user-sign-up" className="btn btn-outline-warning float-right">
                            + Add New User
                        </Link>
                    </div>  */}

                    <div className="col-md-12 table-responsive">
                         <table className="table table-striped table-bordered table-hover">
                            <thead>
                                <tr >
                                    <th>User Name</th>
                                    <th>Email Address</th>
                                    <th>User Type</th>
                                    <th>Role Id</th>
                                    <th>Created Date</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users && users.map((userData, index) => {
                                    // console.log(userData.firstName + ' ' + userData.lastName);
                                    return <tr key={index} >
                                        <td>{userData.role_name }</td>
                                        <td>{userData.user_name}</td>
                                        <td>{userData.user_type}</td>
                                        <td>{userData.roleId}</td>
                                        <td>{userData.created_date}</td>
                                        <td className="actionClass">
                                        <button onClick={() => updateUserRecord(userData.id)}> Update </button>
                                        <button onClick={() => deleteUserRecord(userData.id)}> Delete </button>
                                        </td>
                                    </tr>
                                })}
                            </tbody>
                        </table>
                    </div>

                </div>



    );



}
export default Users;
